#include <stdio.h>
#include <string.h>

int main (int argc, char *argv[] )
{
	char str[9000];
	FILE *file;

	char input[] = "";
	int len = strlen(input);

	file = fopen ( "/home/ttsiapras/Desktop/fuse/example/mountdir/testFile.txt", "w+" );
	if ( file == NULL )
		perror("Could not open file!\n");

	if ( fwrite ( input, sizeof(char), len, file) == 0 )
		perror ("Could not write to file!\n");
	fflush(file);
	fclose ( file);

	file = fopen ( "/home/ttsiapras/Desktop/fuse/example/mountdir/testFile.txt", "r+" );

	fread(str,sizeof(char),len,file);
	fclose ( file);
	str[len] = '\0';
	printf("->%s<-\n",str);
	return 0;
}